    Node * head = (*list) -> head;
    Node * tail = (*list) -> tail;

    if (list == NULL) return -1;

    Node * node = initNode(num);
    if (node == NULL) return -1;

    // empty dlist
    if ((*list) -> head == NULL && (*list) -> tail == NULL) {
        (*list)-> head = node;
